trait Plural {
   def pluralizar(x: String) = x + “s”
}

trait Dupla {
   def duplicar(x: String) = x + x
}

class MyString(x: String) extends Plural 
     with Dupla { }

def getInt(obj: Any) = obj match {
    case x: Int => x
    case s: String => Integer.parseInt(s)
    case _: BigInt => Int.MaxValue
    case _=> 0
}
